# 添加模板
for i, template in enumerate(templates):
    # 检查是否已存在
    exists = any(t['id'] == template['id'] for t in data['templates'])
    if not exists:
        data['templates'].append(template)
        print(f"已添加模板: {template['name']}")
    else:
        print(f"模板已存在，跳过: {template['name']}")

# 保存
with open('data/corporate-promotion.json', 'w', encoding='utf-8') as f:
    json.dump(data, f, ensure_ascii=False, indent=2)

print(f"\n总共 {len(data['templates'])} 个模板")
print("文件已保存")
